<style>
.colorbox{
  width:10%;
  height:10%;
  margin-top:5px;
}
</style>
<template>
  <div id="app-product">
    <div class="product">
          <div class="product-image">
              <img v-bind:src="image"/> 
          </div>
          <div class="product-info">
              <h1>{{brand}}</h1>
              <h1>{{product}}</h1>

              <h2 v-if="inventory > 10">Instock</h2>
              <h2 v-else-if="inventory > 0 && inventory < 10">Almost Out of stock</h2>
              <h2 v-else>Out of stock</h2>

              <ul>
                  <li v-for="detail in details"  v-bind:key="detail.id">
                      {{detail}}
                  </li>
              </ul>
              <div v-for="variant in variants" :key="variant.variantsId" class="colorbox" :style="{backgroundColor:variant.variantsColor}" @mouseover="updateImage(variant.variantsImage)">
                   hi
              </div>

              <button v-on:click="addToCart">Add to cart</button>
              <div>{{cart}}</div>


              money : <input type = "text" v-model = "money" /> <br/><br/>
              LastName : <input type = "text" v-model = "lastname"/> <br/><br/>
              <h1>My name is {{money}} {{lastname}}</h1>
              <h1>Using computed method : {{getfullname}}</h1>


          </div>
     </div>
 </div>
</template>

<script>
export default {
        name: 'HelloWorld',
        props: {
          msg:String
        },
        data(){
          return {
            money :"",
            lastname :"",
            brand:'jockey2',
              product:'socks',
              inventory:11,
              details:["foo","bar"],
              variants:[
                {
                  variantsId:1,
                  variantsColor:"blue",
                  variantsImage:"data:image/jpeg;base64,"
                },
                {
                  variantsId:2,
                  variantsColor:"black",
                  variantsImage:"data:image/jpeg;base64,"
                }
              ],
              cart:0,
              image:null
              
          };
        },
        methods:{
                addToCart(){
                  this.cart = this.cart + 1
                
                },
                updateImage(variantsImage){
                  this.image=variantsImage
                }
              },
              computed :{
                  getfullname : function(){
                 return this.money +" "+ this.lastname;
        }
      }

}

</script>


